static int fipspost_post_ffdh_pairwise_ws(cc_ws_t ws, const ccdh_full_ctx_t full_key)
{
    // This test is not a true POST test, but meets a requirement from FIPS to ensure that, while
    // the pairwise consistency test is valid with the correct key, it then fails when the key
    // material is altered. To ease the certification process, we put this test in the FORCEFAIL
    // case of the POST tests.
    int rv = CCERR_OK;
    ccdh_const_gp_t gp = ccdh_gp_rfc3526group14();

    // We first must verify that the consistency test passes.
    rv = ccdh_pairwise_consistency_check_ws(ws, gp, &ccrng_zero, full_key);
    if (rv) {
        failf("[PCT] CCDH_PAIRWISE_CONSISTENCY: unexpected FAILURE");
    } else {
        debugf("[PCT] CCDH_PAIRWISE_CONSISTENCY: expected SUCCESS");
    }


    // FORCEFAIL and Verify by Pairwise Consistency Check
    ccdh_ctx_x(full_key)[0] ^= 0x01;
    // We then must verify that the consistency test fails once the key has been modified.
    rv = ccdh_pairwise_consistency_check_ws(ws, gp, &ccrng_zero, full_key);
    if (rv) {
        debugf("[PCT] CCDH_PAIRWISE_CONSISTENCY: FORCEFAIL");
        rv = CCERR_OK;
    } else {
        failf("[PCT] CCDH_PAIRWISE_CONSISTENCY: Unexpected SUCCESS");
        rv = CCERR_INTERNAL;
    }

    return rv;
}
